class Layer {
public:
    Layer() {}
    Layer(int input_size, int output_size) {
        this-&gt;weights.resize(input_size, std::vector(output_size));
        this-&gt;biases.resize(output_size);
        for (int i = 0; i &lt; input_size; ++i) {
            for (int j = 0; j &lt; output_size; ++j) {
                this-&gt;weights[i][j] = ((double)rand() / RAND_MAX);
            }
        }
        for (int i = 0; i &lt; output_size; ++i) {
            this-&gt;biases[i] = ((double)rand() / RAND_MAX) ;
        }
    }

    std::vector&gt; weights;
    std::vector biases;
};

class NeuralNet {

public:
    NeuralNet() {

	}

    NeuralNet(const std::vector&amp; layers) {
        // Create layers
        for (size_t i = 1; i &lt; layers.size(); ++i) {
            this-&gt;layers.emplace_back(layers[i - 1], layers[i]);
        }
    }

    double Sigmoid(double x) {
        return 1.0 / (1.0 + exp(-x));
    }

    std::vector Forward(const std::vector&amp; input) {
        std::vector layer_input = input;

        for (Layer&amp; layer : layers) {
            std::vector layer_output(layer.biases.size());
            for (size_t i = 0; i &lt; layer.biases.size(); ++i) {
                double sum = 0;
                for (size_t j = 0; j &lt; layer_input.size(); ++j) {
                    sum += layer_input[j] * layer.weights[j][i];
                }
                sum += layer.biases[i];
                layer_output[i] = Sigmoid(sum);
            }
            layer_input = layer_output;
        }

        return layer_input;
    }

    // Set all parameters
    void SetParams(const std::vector&amp; params) {
        size_t index = 0;
        for (Layer&amp; layer : layers) {
            for (size_t i = 0; i &lt; layer.weights.size(); ++i) {
                for (size_t j = 0; j &lt; layer.weights[i].size(); ++j) {
                    layer.weights[i][j] = params[index++];
                }
            }

            for (size_t i = 0; i &lt; layer.biases.size(); ++i) {
                layer.biases[i] = params[index++];
            }
        }
    }

    std::vector GetParams() {
        std::vector params;
        for (const Layer&amp; layer : layers) {
            for (const std::vector&amp; weights_row : layer.weights) {
                params.insert(params.end(), weights_row.begin(), weights_row.end());
            }

            params.insert(params.end(), layer.biases.begin(), layer.biases.end());
        }

        return params;
    }

    std::vector layers;
};

class GeneticAlgorithm {
public:
    GeneticAlgorithm(int population_size, int num_params, double mutation_rate, const NeuralNet&amp; neural_net){
        this-&gt;population_size = population_size;
        this-&gt;num_params = num_params;
        this-&gt;mutation_rate = mutation_rate;
        this-&gt;neural_net = neural_net;

        for (int i = 0; i &lt; population_size; i++) {
            std::vector individual(num_params);
            for (int j = 0; j &lt; num_params; j++) {
                individual[j] = ((double)rand() / RAND_MAX) * 2 - 1;
            }
            this-&gt;population.push_back(individual);
        }
    }

    int BinaryTournament() {
        int i = rand() % this-&gt;population_size;
        int j = rand() % this-&gt;population_size;
        return (Fitness(i) &lt; Fitness(j)) ? i : j;
    }

    double Fitness(int index) {
        std::vector params = this-&gt;population[index];
        this-&gt;neural_net.SetParams(params);
        double error = 0;

        // Calculate error for all input combinations
        for (int i = 0; i &lt; 2; i++) {
            for (int j = 0; j &lt; 2; j++) {
                double input[2] = { i, j };
                double output = this-&gt;neural_net.Forward(std::vector(input, input + 2))[0];
                double target = (i||j)?1.0:0.0;
                error += pow(target - output,2);
            }
        }

        return error;
    }

    void Mutate(std::vector&amp; individual) {
        for (int i = 0; i &lt; this-&gt;num_params; i++) {
            if (((double)rand() / RAND_MAX) &lt; this-&gt;mutation_rate) {
                individual[i] += ((double)rand() / RAND_MAX) * 2 - 1;
            }
        }
    }

    std::vector Crossover(std::vector parent1, std::vector parent2) {
        int crossover_point = rand() % this-&gt;num_params;
        std::vector child(this-&gt;num_params);

        for (int i = 0; i &lt; crossover_point; i++) {
            child[i] = parent1[i];
        }

        for (int i = crossover_point; i &lt; this-&gt;num_params; i++) {
            child[i] = parent2[i];
        }

        return child;
    }

    std::vector&gt; Epoch() {
        std::vector&gt; new_population;

        // 生成新种群
        for (int i = 0; i &lt; this-&gt;population_size; i++) {
            int parent1_index = BinaryTournament();
            int parent2_index = BinaryTournament();

            std::vector parent1 = this-&gt;population[parent1_index];
            std::vector parent2 = this-&gt;population[parent2_index];

            std::vector child = Crossover(parent1, parent2);
            Mutate(child);

            new_population.push_back(child);
        }

        return new_population;
    }

    NeuralNet neural_net;
    int population_size;
    int num_params;
    double mutation_rate;
    std::vector&gt; population;
};


int main() {
    srand(time(NULL));

    std::vector layers = { 2,3,5,3,1 }; // 尝试修改网络结构，例如：{ 2, 4, 1 }，表示有2个输入，4个隐藏层神经元和1个输出。
    NeuralNet nn(layers);

    std::vector initial_params = nn.GetParams();
    int num_params = initial_params.size();
    GeneticAlgorithm ga(50, num_params, 0.1, nn);

    int population_size = ga.population_size;

    std::vector, double&gt;&gt; training_set = {
        {{0, 1}, 1},
        {{1, 0}, 1},
        {{0, 0}, 0},
        {{1, 1}, 1}
    };
    std::vector&gt; new_population;

    // 进行 1000 代进化
        for (int i = 0; i &lt; 1000; i++) {
            // 计算每个个体的适应度
            std::vector fitness_values;
            for (int j = 0; j &lt; population_size; j++) {
                nn.SetParams(ga.population[j]);
                double fitness = 0;
                for (int k = 0; k &lt; training_set.size(); k++) {
                    std::vector input = training_set[k].first;
                    double target = training_set[k].second;
                    double output = nn.Forward(input)[0];
                    fitness += (target - output);
                }
                fitness_values.push_back(fitness);
            }

            // 输出最佳个体的适应度
            double best_fitness = *std::max_element(fitness_values.begin(), fitness_values.end());
            std::cout &lt;&lt; "Generation " &lt;&lt; i &lt;&lt; ", best fitness: " &lt;&lt; best_fitness &lt;&lt; std::endl;

            // 使用遗传算法进行进化
            new_population = ga.Epoch();

            // 更新种群
            ga.population = new_population;
        }

    // 输出最佳个体的参数和适应度
    std::vector best_params = ga.population[0];
    double best_fitness = 0;
    nn.SetParams(best_params);
    for (int i = 0; i &lt; training_set.size(); i++) {
        std::vector input = training_set[i].first;
        double target = training_set[i].second;
        double output = nn.Forward(input)[0];
        double error = (target - output);
        best_fitness += error;
        std::cout &lt;&lt; "Input: " &lt;&lt; input[0] &lt;&lt; " " &lt;&lt; input[1] &lt;&lt; std::endl;
        std::cout &lt;&lt; "Output: " &lt;&lt; output &lt;&lt; std::endl;
        std::cout &lt;&lt; "Target: " &lt;&lt; target &lt;&lt; std::endl;
        std::cout &lt;&lt; "------------- " &lt;&lt; target &lt;&lt; std::endl;
    }
    std::cout &lt;&lt; "Best fitness: " &lt;&lt; best_fitness &lt;&lt; std::endl;
    std::cout &lt;&lt; "Best parameters: ";
    for (int i = 0; i &lt; best_params.size(); i++) {
        std::cout &lt;&lt; best_params[i] &lt;&lt; " ";
    }
    std::cout &lt;&lt; std::endl;

    return 0;
}

