const bodyParser = require('body-parser');
const crypto = require('crypto');

// Initialize the app
const app = express();

// Use body-parser to parse the request body
app.use(bodyParser.json());

// Set the webhook secret
const webhookSecret = 'your-webhook-secret';

// Set the route to listen for webhook events
app.post('/webhook', (req, res) =&gt; {
  // Get the event data
  const event = req.body;

  // Check the webhook signature
  const signature = req.headers['x-cc-webhook-signature'];
  const calculatedSignature = crypto.createHmac('sha256', webhookSecret)
    .update(JSON.stringify(event))
    .digest('hex');

  if (signature !== calculatedSignature) {
    // The signatures do not match, so return an error
    return res.status(400).send('Invalid webhook signature');
  }

  // The signatures match, so process the event
  switch (event.type) {
    case 'charge:success':
      console.log('A charge was successful!');
      break;
    case 'refund:success':
      console.log('A refund was successful!');
      break;
    default:
      console.log('An unknown event occurred: ', event.type);
  }

  // Return a success response
  return res.sendStatus(200);
});

// Start the server
app.listen(3000, () =&gt; {
  console.log('Webhook listener listening on port 3000!');
});
