def isnand(d):
    """Check if any tensor in dictionary contains nan values."""
    return any([v.isnan().any().item() for v in d.values() if isinstance(v, torch.Tensor)])


def collate_fn(max_size, min_seq=2, float16=True, shm=False):

    #barray = bytearray(bsize)
    batch0 = []

    def collate_fn0(batch):

        nonlocal max_size
        nonlocal min_seq
        nonlocal batch0
        #nonlocal barray

        batch = [d for d in batch if not isnand(d)]

        if float16:
            batch = [{k: to_float16(v) for k, v in d.items()} for d in batch]

        batch0.extend(batch)
        batch1 = []

        n0 = 0
        while batch0:

            n1 = batch0[-1]['ndvp'].item()

            # Filter out 1 pip samples (made of just the cart item).
            if n1 &lt; min_seq and len(batch0) &gt; 1:
                batch0.pop(-1)
                continue

            if n0 + n1 &gt; max_size:
                break

            batch1.append(batch0.pop(-1))
            n0 += n1

        keys = list(batch1[0].keys())

        d = {}

        for k in keys:

            if k == 'meta':
                d[k] = [b[k] for b in batch1]
            else:
                d[k] = torch.cat([b[k] for b in batch1])

        batch0 = batch0[-max_size:]

        if shm:
            path = '/dev/shm/%s.batch' % utils.o2h2(random.random())
            utils.pickle_dump(d, path)
            return {'path': path}

        return d

    return collate_fn0

